import './App.css';
import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
  Navigate
} from "react-router-dom";

// toy website

const users = ["Holly", "Steve", "Jason", "Paula"];

function App() {
  return (
    <div>    
      <Router>
        <div>
          <div id="hero">
            <h1>ElliotMB</h1>
            <nav>
            <ul className="nav-bar">
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/users">Users</Link>
              </li>
            </ul>
            </nav>
          </div>

          <Routes>
            <Route path="/" element={<Home/>} />
            <Route path="/users" element={<Users/>}/>
            <Route path="/users/:userClass/*" element={<UserPage/>} />
            <Route path="/about" element={<About/>} />
            <Route path="/not-found" element={<NotFound/>}/>
            <Route path={"*"} element={<NotFound/>}/>
          </Routes>
        </div>
      </Router>
    </div>
  )
}

function Home() {
  return(
    <div>
      <h2>Home</h2>
      <p>Hi hi this is my epic homepage ^_^</p>
    </div>
  )
}

function About() {
  return(
    <div>
      <h2>About</h2>
      <p>Hi hi this is my epic about page ^3^</p>
    </div>
  )
}

function Users() {
  return(
    <div>
      <h2>Users</h2>
      <nav>
        <ul>
          {LinkList("/users", users)}
        </ul>
      </nav>
    </div>
  )
}

function UserPage(){
  const { userClass } = useParams();
  if(!users.reduce((x, y) => x || (y === userClass), false)){
    return <Navigate to={"/not-found"}/>
  }
  return (
    <div>
      <h2>User</h2>
      <p>{userClass}</p>
    </div>
  )
}

function NotFound(){
  return (
    <div>
      <h2>Oopsie doopsie! That page doesn't exist!</h2>
      <Link to="/">Go Home</Link> 
      {/* this doesnt need a Routes element. 
      As we are already inside one
      it will update when we change the url*/}
    </div>
  )
}

function LinkList(root, lis) {
  let elements = [];
  lis.forEach((li, i) => {
    elements.push(
      <li key={i}>
      <Link to={`${root}/${li}`}>{li}</Link>
      </li>
    );
  });

  return elements;
}

export default App;
