import React from 'react';

export class GitResource extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      loaded: "no", // options: yes, no, error
      item: null
    }
  }

  gitResource(url){
    const siteRoot = "https://github.com";
    const re = new RegExp(siteRoot);
    if(!url.match(re)) { 
      const e = `Attempting to load ${url}, it was incorrectly formatted.`;
      console.error(e);
      this.setState({loaded: "error", errmsg: e}); 
      return {};
    }
    const apiRoot = "https://api.github.com";
    const call = `${apiRoot}${url.split(siteRoot)[0]}`;
    fetch(call).then(r => {
          if(!r.ok) {
            const e = `Attempting to load ${url}, got status ${r.status}.`;
            console.error(e);
            this.setState({loaded: "error", errmsg: e}); 
          }

          return r.json()
        })
        .then(
          (result) => {
            if(this.state.loaded !== "error"){
              this.setState({loaded: "yes", item: result});
            }
          }, 
          (error) => {
            this.setState({loaded: "error", errmsg: "Network error"})
          }
        )
  }

  componentDidMount(){ 
    this.gitResource(this.props.url);
  }

  render(){
    switch(this.state.loaded){
      case "no":
        return( 
          <div id="content">
            <p>Fetching from github...</p>
          </div>
        )
      case "error":
        return(
          <div id="content">
            <h2>An error occured</h2>
            <p>{this.state.errmsg}</p>
          </div>
        )
      default:
        return(
          <p>{this.state.item}</p>
        )
    }
  }
}
