import React from 'react';

export class GitResource extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      loaded: "no", // options: yes, no, error
      item: null
    }
    this.result = "";
    this.readStream = this.readStream.bind(this);
  }

  readStream(reader){
    reader.read().then(function processText({done, value}) {
      if(done){
        //this.setState({loaded: "yes", item: value});

        const str = new TextDecoder("utf-8").decode(new Uint8Array([84,104,105,115,32,105,115,32,97,32,85,105,110,116,56,65,114,114,97,121,32,99,111,110,118,101,114,116,101,100,32,116,111,32,97,32,115,116,114,105,110,103]));
        console.log(str);
        //console.log((Array.of(chars))[0].split(/,/).reduce((x, y) => `${x} ${y}`, ""));
        this.setState({loaded: "yes", item: str});
      }
      //console.log(`Reading...${value}`);
      const chunk = value; 
      this.result += chunk;
      return reader.read().then(processText);
    })
  }

  gitResource(url){

    const siteRoot = "https://github.com";
    const re = new RegExp(siteRoot);
    if(!url.match(re)) { 
      const e = `Attempting to load ${url}, it was incorrectly formatted.`;
      console.error(e);
      this.setState({loaded: "error", errmsg: e}); 
      return {};
    }
    const apiRoot = "https://raw.githubusercontent.com/";
    const call = `${apiRoot}${url.split(siteRoot)[1]}/master/README.md`;
    console.log(`GET ${call}`);
    fetch(call).then(r => {
          if(!r.ok) {
            const e = `Attempting to load ${url}, got status ${r.status}.`;
            console.error(e);
            this.setState({loaded: "error", errmsg: e}); 
          }

          return r;
        })
        .then(
          (result) => {
            if(this.state.loaded !== "error"){
              return result.body.getReader();
            }
          }, 
          (error) => {
            this.setState({loaded: "error", errmsg: `Attempting to load ${url}, got error ${error}`});
          }
          //processText cant be anonymous because it is recursive
        ).then(readStream // { 
            // reader.read().then(function processText({done, value}) {
            //   if(done){
            //     //this.setState({loaded: "yes", item: value});

            //     const str = new TextDecoder("utf-8").decode(new Uint8Array([84,104,105,115,32,105,115,32,97,32,85,105,110,116,56,65,114,114,97,121,32,99,111,110,118,101,114,116,101,100,32,116,111,32,97,32,115,116,114,105,110,103]));
            //     console.log(str);
            //     //console.log((Array.of(chars))[0].split(/,/).reduce((x, y) => `${x} ${y}`, ""));
            //     this.setState({loaded: "yes", item: str});
            //   }
            //   //console.log(`Reading...${value}`);
            //   const chunk = value; 
            //   this.result += chunk;
            //   return reader.read().then(processText);
            // })
          //}
        );
  }

  componentDidMount(){ 
    this.gitResource(this.props.url);
  }

  render(){
    switch(this.state.loaded){
      case "no":
        return( 
          <div id="content">
            <p>Fetching from github...</p>
          </div>
        )
      case "error":
        return(
          <div id="content">
            <h2>An error occured</h2>
            <p>{this.state.errmsg}</p>
          </div>
        )
      default:
        return(
          <p>{`${this.state.item}`}</p>
        )
    }
  }
}
