import React, { useEffect } from 'react';
import "./Project.css"
import {
  Navigate
} from "react-router-dom";

import {useParams} from "react-router-dom";
import {projects} from "../../data/projects.js"
import {gitResource} from "../../requests/gitResource.js";

export class Project extends React.Component {
  constructor(props){
    super(props);
    this.hook = useParams();
    this.state = {
      project: null,
      loaded: "no", // options: yes, no, error, not-found
      item: null
    }
  }

  gitResource(url){
    const siteRoot = "https://github.com";
    const re = new RegExp(siteRoot);
    if(!url.match(re)) { 
      console.error(`Attempting to load ${url}, it was incorrectly formatted.`);
      return {"error": `bad formatting in url ${url}`}; 
    }
    const apiRoot = "https://api.github.com";
    const call = `${apiRoot}${url.split(siteRoot)[0]}`;
    return fetch(call).then(r => {
          if(!r.ok) {
            console.error(`Attempting to load ${url}, got status ${r.status}.`);
            return {"error": `status ${r.status}`};
          }

          return r.json()
        }
      );
  }

  componentDidMount(){ //cant be called after the return as react hooks are tempermental
    const { projectClass } = this.hook;
    let projectID = 0;
    if(!projects.reduce((x, y) => { 
      if(!x) { projectID++; } 
      return (x || y.name === projectClass);
    }, false)) { this.setState({
        project: null, 
        loaded: "not-found",
        item: null
    })}else{
      this.setState({
        project: projects[projectID],
        loaded: "no",
        item: null
      });
    }

    document.title = `${projectClass} | Elliot Buckingham`;
  }

  render(){
    <div id="content">
      <h2>{this.props.match.params.projectClass}</h2>
      <p>{this.props.globalStore.projectClass}</p>
    </div>
